import React, { useState } from "react";
import campaigns from "./campaigns";
import statesDistricts from "./statesDistricts.json";
import axios from "axios";
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    state: "",
    district: "",
    campaignsData: {},
  });
  const [showPopup, setShowPopup] = useState(false);
  const [filteredData, setFilteredData] = useState({});

  const handleFormChange = (e, id, field) => {
    const { name, value } = e.target;
    if (field === "campaign") {
      setFormData((prev) => ({
        ...prev,
        campaignsData: {
          ...prev.campaignsData,
          [id]: { ...prev.campaignsData[id], amount: value },
        },
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const filtered = Object.keys(formData.campaignsData)
      .filter((key) => formData.campaignsData[key]?.amount)
      .reduce((acc, key) => {
        acc[key] = formData.campaignsData[key];
        return acc;
      }, {});
    if (Object.keys(filtered).length === 0) {
      alert("Please fill in at least one campaign amount!");
      return;
    }
    setFilteredData({
      state: formData.state,
      district: formData.district,
      campaignsData: filtered,
    });
    setShowPopup(true);
  };

  const handleConfirm = async () => {
    try {
      await axios.post("http://localhost:8001/update", filteredData);
      alert("Data submitted successfully!");
      setFormData({ state: "", district: "", campaignsData: {} });
      setShowPopup(false);
    } catch (error) {
      alert("Submission failed! Please try again.");
    }
  };

  const handleCancel = () => setShowPopup(false);

  const districtsForState = statesDistricts
    .filter((item) => item.STATE === formData.state)
    .map((item) => item.DISTRICT);

  return (
    <div className="app-container">
      <h1>Campaign Data Submission</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>State:</label>
          <select
            name="state"
            value={formData.state}
            onChange={(e) => handleFormChange(e)}
          >
            <option value="">-- Select State --</option>
            {[...new Set(statesDistricts.map((item) => item.STATE))].map(
              (state) => (
                <option key={state} value={state}>
                  {state}
                </option>
              )
            )}
          </select>
        </div>

        <div className="form-group">
          <label>District:</label>
          <select
            name="district"
            value={formData.district}
            onChange={(e) => handleFormChange(e)}
            disabled={!formData.state}
          >
            <option value="">-- Select District --</option>
            {districtsForState.map((district) => (
              <option key={district} value={district}>
                {district}
              </option>
            ))}
          </select>
        </div>

        {campaigns.map((campaign) => (
          <div key={campaign.id} className="form-group">
            <label>{campaign.name}:</label>
            <input
              type="number"
              placeholder="Enter amount"
              value={
                formData.campaignsData[campaign.id]?.amount || ""
              }
              onChange={(e) =>
                handleFormChange(e, campaign.id, "campaign")
              }
            />
          </div>
        ))}

        <button type="submit">Submit</button>
      </form>

      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h2>Confirm Submission</h2>
            <p>Are you sure you want to submit the following data?</p>
            <pre>{JSON.stringify(filteredData, null, 2)}</pre>
            <div>
              <button onClick={handleConfirm}>Confirm</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
