// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";
import Select from "react-select"; // Import react-select
import campaigns from "./campaigns"; // Import the campaigns list
import statesDistricts from "./statesDistricts.json"; // Import the JSON file for states and districts
import Login from "./components/Login"; // Import the Login component
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    month: "",
    state: "",
    district: "",
    village: "",
    customVillage: "",
    block: "",
    campaignsData: {},
  });

  const [states, setStates] = useState([]);
  const [districtOptions, setDistrictOptions] = useState([]);
  const [villageOptions, setVillageOptions] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [submittedData, setSubmittedData] = useState(null);

  const months = [
    "January '25", "February '25", "March '25", "April '25", "May '25", "June '25", "July '25", "August '25", "September '25", "October '25", "November '25", "December '24",
  ];

  useEffect(() => {
    const uniqueStates = [
      ...new Set(statesDistricts.map((item) => item.STATE.toLowerCase())),
    ];
    setStates(uniqueStates.sort());

    const initialCampaignsData = {};
    campaigns.forEach((campaign) => {
      initialCampaignsData[campaign.id] = { quantity: "", amount: "" };
    });
    setFormData((prev) => ({ ...prev, campaignsData: initialCampaignsData }));
  }, []);

  const handleStateChange = (selectedOption) => {
    const state = selectedOption.value;
    setFormData((prevData) => ({ ...prevData, state }));
    const districtOptions = statesDistricts
      .filter((item) => item.STATE.toLowerCase() === state)
      .map((item) => ({ value: item.DISTRICT.toLowerCase(), label: item.DISTRICT }));
    setDistrictOptions(districtOptions);
  };

  const handleDistrictChange = (selectedOption) => {
    const district = selectedOption.value;
    setFormData((prevData) => ({ ...prevData, district }));
    const villageOptions = statesDistricts
      .filter((item) => item.DISTRICT.toLowerCase() === district)
      .map((item) => ({ value: item.VILLAGE.toLowerCase(), label: item.VILLAGE }));
    setVillageOptions(villageOptions);
  };

  const handleVillageChange = (selectedOption) => {
    const village = selectedOption.value;
    setFormData((prevData) => ({ ...prevData, village }));
  };

  const handleCustomVillageChange = (e) => {
    setFormData((prevData) => ({ ...prevData, customVillage: e.target.value }));
  };

  const handleCampaignChange = (campaignId, field, value) => {
    setFormData((prevData) => {
      const campaignsData = { ...prevData.campaignsData };
      campaignsData[campaignId][field] = value;
      return { ...prevData, campaignsData };
    });
  };

  const handleSubmit = () => {
    setShowPopup(true);
    setSubmittedData(formData);
    setFormData({
      month: "",
      state: "",
      district: "",
      village: "",
      customVillage: "",
      block: "",
      campaignsData: {},
    });
  };

  const isLoggedIn = localStorage.getItem("loggedIn") === "true";

  return (
    <Router>
      <div className="container">
        <Routes>
          <Route
            path="/"
            element={isLoggedIn ? <Navigate to="/app" /> : <Login />}
          />
          <Route
            path="/app"
            element={isLoggedIn ? (
              <div>
                <h1>Service Campaign Form</h1>
                <h2>Year - 2025</h2>
                <img src="/msg.jpg" alt="msg" />

                <form>
                  <div>
                    <label>Month</label>
                    <Select
                      options={months.map((month) => ({
                        value: month,
                        label: month,
                      }))}
                      onChange={(selectedOption) => {
                        setFormData((prevData) => ({ ...prevData, month: selectedOption.value }));
                      }}
                    />
                  </div>

                  <div>
                    <label>State</label>
                    <Select
                      options={states.map((state) => ({
                        value: state.toLowerCase(),
                        label: state,
                      }))}
                      onChange={handleStateChange}
                    />
                  </div>

                  <div>
                    <label>District</label>
                    <Select
                      options={districtOptions}
                      onChange={handleDistrictChange}
                    />
                  </div>

                  <div>
                    <label>Village</label>
                    <Select
                      options={villageOptions}
                      onChange={handleVillageChange}
                    />
                    <div>
                      <label>Or, Custom Village</label>
                      <input
                        type="text"
                        value={formData.customVillage}
                        onChange={handleCustomVillageChange}
                      />
                    </div>
                  </div>

                  {campaigns.map((campaign) => (
                    <div key={campaign.id}>
                      <h3>{campaign.name}</h3>
                      <div>
                        <label>Quantity</label>
                        <input
                          type="number"
                          value={formData.campaignsData[campaign.id]?.quantity || ""}
                          onChange={(e) =>
                            handleCampaignChange(campaign.id, "quantity", e.target.value)
                          }
                        />
                      </div>
                      <div>
                        <label>Amount</label>
                        <input
                          type="number"
                          value={formData.campaignsData[campaign.id]?.amount || ""}
                          onChange={(e) =>
                            handleCampaignChange(campaign.id, "amount", e.target.value)
                          }
                        />
                      </div>
                    </div>
                  ))}

                  <button type="button" onClick={handleSubmit}>Submit</button>
                </form>

                {showPopup && (
                  <div className="popup">
                    <h3>Submitted Data</h3>
                    <pre>{JSON.stringify(submittedData, null, 2)}</pre>
                  </div>
                )}
              </div>
            ) : (
              <Navigate to="/" />
            )}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
