import React, { useState, useEffect } from "react";
import axios from "axios";
import campaigns from "./campaigns";
import statesDistricts from "./statesDistricts.json";
import "./App.css";
import Login from "./components/Login";
import AdminPage from "./components/AdminPage";
import LocationDetails from "./components/LocationDetails";
import CampaignDetails from "./components/CampaignDetails";
import "./components/login.css";

const App = () => {
  const [formData, setFormData] = useState({
    month: "",
    state: "",
    district: "",
    village: "",
    customVillage: "",
    block: "",
    campaignsData: {},
  });

  const [states, setStates] = useState([]);
  const [districtOptions, setDistrictOptions] = useState([]);
  const [villageOptions, setVillageOptions] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [submittedData, setSubmittedData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState(""); // Store the role (admin/user)

  // Initialize states and campaign data
  useEffect(() => {
    const uniqueStates = [
      ...new Set(statesDistricts.map((item) => item.STATE.toLowerCase())),
    ];
    setStates(uniqueStates.sort());

    const initialCampaignsData = campaigns.reduce((acc, campaign) => {
      acc[campaign.id] = { quantity: "", amount: "" };
      return acc;
    }, {});
    setFormData((prev) => ({ ...prev, campaignsData: initialCampaignsData }));
  }, []);

  // Handle login and set user role
  const handleLogin = (userRole) => {
    setIsLoggedIn(true);
    setRole(userRole);
  };

  // Handle state selection
  const handleStateChange = (e) => {
    const selectedState = e.target.value.toLowerCase();
    setFormData({ ...formData, state: selectedState, district: "", village: "" });

    const filteredDistricts = [
      ...new Set(
        statesDistricts
          .filter((item) => item.STATE.toLowerCase() === selectedState)
          .map((item) => item.DISTRICT.toLowerCase())
      ),
    ].sort();

    const districtOptions = filteredDistricts.map((district) => ({
      value: district,
      label: district.charAt(0).toUpperCase() + district.slice(1),
    }));
    setDistrictOptions(districtOptions);
  };

  // Handle district selection
  const handleDistrictChange = (selectedOption) => {
    const selectedDistrict = selectedOption.value;
    setFormData({ ...formData, district: selectedDistrict, village: "" });

    const filteredVillages = [
      ...new Set(
        statesDistricts
          .filter((item) => item.DISTRICT.toLowerCase() === selectedDistrict)
          .map((item) => (typeof item.Village === "string" ? item.Village.toLowerCase() : ""))
      ),
    ].sort();

    const villageOptions = filteredVillages.map((village) => ({
      value: village,
      label: village.charAt(0).toUpperCase() + village.slice(1),
    }));
    villageOptions.push({ value: "other", label: "Other" });
    setVillageOptions(villageOptions);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for required fields
    if (!formData.month || !formData.state || !formData.district || !formData.village) {
      alert("Please fill all the required fields.");
      return;
    }

    setSubmittedData(formData);
    setShowPopup(true);
  };

  // Confirm and send data to server
  const handleConfirm = async () => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8001";
      const response = await axios.put(`${API_URL}/update`, submittedData);
      alert(response.data.message);

      setFormData({
        month: "",
        state: "",
        district: "",
        village: "",
        customVillage: "",
        block: "",
        campaignsData: campaigns.reduce((acc, campaign) => {
          acc[campaign.id] = { quantity: "", amount: "" };
          return acc;
        }, {}),
      });
      setShowPopup(false);
    } catch (error) {
      console.error("Error updating the data:", error);
      alert("Failed to update the data.");
    }
  };

  // Cancel submission
  const handleCancel = () => {
    setShowPopup(false);
    setFormData({
      month: "",
      state: "",
      district: "",
      village: "",
      customVillage: "",
      block: "",
      campaignsData: campaigns.reduce((acc, campaign) => {
        acc[campaign.id] = { quantity: "", amount: "" };
        return acc;
      }, {}),
    });
  };

  // Filter campaigns data for display
  const getFilteredData = (data) => {
    const filteredCampaigns = Object.entries(data.campaignsData || {}).filter(
      ([_, details]) => details.quantity || details.amount
    );

    return {
      ...data,
      campaignsData: Object.fromEntries(filteredCampaigns),
    };
  };

  const filteredData = submittedData ? getFilteredData(submittedData) : null;

  // Render components based on user role
  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  if (role === "admin") {
    return <AdminPage />;
  }

  return (
    <div className="container">
      <h1>Service Campaign Form</h1>
      <form onSubmit={handleSubmit}>
        <LocationDetails
          formData={formData}
          setFormData={setFormData}
          states={states}
          districtOptions={districtOptions}
          villageOptions={villageOptions}
          handleStateChange={handleStateChange}
          handleDistrictChange={handleDistrictChange}
        />
        <CampaignDetails
          campaigns={campaigns}
          formData={formData}
          handleFormChange={(e, id, field) =>
            setFormData((prev) => ({
              ...prev,
              campaignsData: {
                ...prev.campaignsData,
                [id]: { ...prev.campaignsData[id], [field]: e.target.value },
              },
            }))
          }
        />
        <button type="submit">Submit</button>
      </form>

      {showPopup && filteredData && (
        <div className="popup">
          <div className="popup-content">
            <h3>Confirm Submission</h3>
            <table>
              <tbody>
                <tr>
                  <th>Month</th>
                  <td>{filteredData.month}</td>
                </tr>
                <tr>
                  <th>State</th>
                  <td>{filteredData.state}</td>
                </tr>
                <tr>
                  <th>District</th>
                  <td>{filteredData.district}</td>
                </tr>
                <tr>
                  <th>Village</th>
                  <td>{filteredData.village === "other" ? filteredData.customVillage : filteredData.village}</td>
                </tr>
              </tbody>
            </table>
            <h3>Campaign Details</h3>
            <table>
              <thead>
                <tr>
                  <th>Campaign Name</th>
                  <th>Quantity</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(filteredData.campaignsData).map(([id, details]) => {
                  const campaign = campaigns.find((c) => c.id === parseInt(id));
                  return (
                    <tr key={id}>
                      <td>{campaign ? campaign.name : "Unknown"}</td>
                      <td>{details.quantity}</td>
                      <td>{details.amount}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
            <button onClick={handleConfirm}>Confirm</button>
            <button onClick={handleCancel}>Edit</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
