import React, { useState, useEffect } from "react";
import axios from "axios";
import campaigns from "./campaigns"; // Import the campaigns list
import statesDistricts from "./statesDistricts.json"; // Import the JSON file for states and districts
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    month: "",
    state: "",
    district: "",
    village: "",
    block: "",
    campaignsData: {}, // Campaign-specific form fields
  });

  const [states, setStates] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // Control the popup visibility
  const [submittedData, setSubmittedData] = useState(null); // Store submitted data for popup display

  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  useEffect(() => {
    // Extract unique states and initialize campaignsData
    const uniqueStates = [...new Set(statesDistricts.map((item) => item.STATE))];
    setStates(uniqueStates);

    const initialCampaignsData = {};
    campaigns.forEach((campaign) => {
      initialCampaignsData[campaign.id] = { quantity: "", amount: "" };
    });
    setFormData((prev) => ({ ...prev, campaignsData: initialCampaignsData }));
  }, []);

  const handleStateChange = (e) => {
    const selectedState = e.target.value;
    setFormData({ ...formData, state: selectedState, district: "" });

    const filteredDistricts = statesDistricts
      .filter((item) => item.STATE === selectedState)
      .map((item) => item.DISTRICT);
    setDistricts(filteredDistricts);
  };

  const handleFormChange = (e, campaignId, field) => {
    const value = e.target.value;

    setFormData((prev) => ({
      ...prev,
      campaignsData: {
        ...prev.campaignsData,
        [campaignId]: {
          ...prev.campaignsData[campaignId],
          [field]: value,
        },
      },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedData(formData); // Store form data to display in the popup
    setShowPopup(true); // Show the confirmation popup
  };

  const handleConfirm = async () => {
    try {
      const response = await axios.put(
        "http://localhost:8001/update",
        submittedData
      );
      alert(response.data.message);
      // Clear the form after successful submission
      setFormData({
        month: "",
        state: "",
        district: "",
        village: "",
        block: "",
        campaignsData: campaigns.reduce((acc, campaign) => {
          acc[campaign.id] = { quantity: "", amount: "" };
          return acc;
        }, {}),
      });
      setShowPopup(false); // Close the popup
    } catch (error) {
      console.error("Error updating the data:", error);
      alert("Failed to update the data.");
    }
  };

  const handleCancel = () => {
    setShowPopup(false); // Close the popup without submitting
  };

  const getFilteredData = (data) => {
    const filteredCampaigns = Object.entries(data.campaignsData || {}).filter(
      ([_, details]) => details.quantity || details.amount
    );

    return {
      ...data,
      campaignsData: Object.fromEntries(filteredCampaigns), // Only campaigns with data
    };
  };

  const filteredData = submittedData ? getFilteredData(submittedData) : null;

  return (
    <div className="container">
      <h1>Service Campaign Form</h1>
      <form onSubmit={handleSubmit}>
        {/* General Location Fields */}
        <div className="white-box">
          <h2>Location Details</h2>
          <label>Month</label>
          <select
            value={formData.month}
            onChange={(e) => setFormData({ ...formData, month: e.target.value })}
            required
          >
            <option value="">Select a month</option>
            {months.map((month) => (
              <option key={month} value={month}>
                {month}
              </option>
            ))}
          </select>

          <label>State</label>
          <select value={formData.state} onChange={handleStateChange} required>
            <option value="">Select a state</option>
            {states.map((state) => (
              <option key={state} value={state}>
                {state}
              </option>
            ))}
          </select>

          <label>District</label>
          <select
            value={formData.district}
            onChange={(e) =>
              setFormData({ ...formData, district: e.target.value })
            }
            required
          >
            <option value="">Select a district</option>
            {districts.map((district) => (
              <option key={district} value={district}>
                {district}
              </option>
            ))}
          </select>

          <label>Village/Zone</label>
          <input
            type="text"
            value={formData.village}
            onChange={(e) =>
              setFormData({ ...formData, village: e.target.value })
            }
            required
          />

          <label>Block</label>
          <input
            type="text"
            value={formData.block}
            onChange={(e) =>
              setFormData({ ...formData, block: e.target.value })
            }
            required
          />
        </div>

        {/* Campaign Details */}
        <div className="white-box">
          <h2>Campaign Details</h2>
          {campaigns.map((campaign) => (
            <div key={campaign.id} className="campaign-row">
              <h3>
                {campaign.id}. {campaign.name}
              </h3>
                <input
                  type="number"
                  min="0"
                  placeholder="Quantity"
                  value={formData.campaignsData[campaign.id]?.quantity || ""}
                  onChange={(e) => handleFormChange(e, campaign.id, "quantity")}
                />
                <input
                  type="number"
                  min="0"
                  placeholder="Amount"
                  value={formData.campaignsData[campaign.id]?.amount || ""}
                  onChange={(e) => handleFormChange(e, campaign.id, "amount")}
                />
            </div>
          ))}
        </div>

        <button type="submit">Submit</button>
      </form>

      {showPopup && filteredData && (
        <div className="popup">
          <div className="popup-content">
            <h3>Confirm Submission</h3>
            {/* Display submitted data */}
            {filteredData.month && <p>Month: {filteredData.month}</p>}
            {/* Add more fields here */}
            <button onClick={handleConfirm}>Yes, Submit</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
