import React, { useState, useEffect } from 'react';

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ contact_number: "", name: "", address: "", password: "", role: "user", enable: true });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:8001/users');
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        const data = await response.json();
        setUsers(data);
      } catch (err) {
        setError(err.message || 'Error fetching users');
      }
    };

    fetchUsers();
  }, []);

  const handleAddUser = async () => {
    const { contact_number, name, address, password, role, enable } = newUser;
    if (!contact_number || !name || !address || !password) {
      setError("All fields are required.");
      return;
    }

    try {
      const response = await fetch('http://localhost:8001/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...newUser, enable }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || 'Failed to add user');
      }

      const addedUser = await response.json();
      setUsers((prevUsers) => [...prevUsers, addedUser]);
      setNewUser({ contact_number: "", name: "", address: "", password: "", role: "user", enable: true });
      setError("");
    } catch (err) {
      setError(err.message || 'Error adding user');
    }
  };

  const handleRemoveUser = async (contact_number) => {
    try {
      const response = await fetch(`http://localhost:8001/users/${contact_number}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to remove user');
      }

      setUsers((prevUsers) => prevUsers.filter((user) => user.contact_number !== contact_number));
    } catch (err) {
      setError(err.message || 'Error removing user');
    }
  };

  const handleToggleEnable = async (contact_number, currentStatus) => {
    try {
      const response = await fetch(`http://localhost:8001/users/${contact_number}/enable`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ enable: !currentStatus }),
      });

      if (!response.ok) {
        throw new Error('Failed to update enable status');
      }

      setUsers((prevUsers) => prevUsers.map((user) => {
        if (user.contact_number === contact_number) {
          return { ...user, enable: !currentStatus };
        }
        return user;
      }));
    } catch (err) {
      setError(err.message || 'Error toggling enable status');
    }
  };

  const handleRoleChange = async (contact_number, role) => {
    try {
      const response = await fetch(`http://localhost:8001/users/${contact_number}/role`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ role }),
      });

      if (!response.ok) {
        throw new Error('Failed to update role');
      }

      setUsers((prevUsers) => prevUsers.map((user) => {
        if (user.contact_number === contact_number) {
          return { ...user, role };
        }
        return user;
      }));
    } catch (err) {
      setError(err.message || 'Error updating role');
    }
  };

  return (
    <div>
      <h1>Admin Page</h1>
      <div>
        <input
          type="text"
          value={newUser.contact_number}
          onChange={(e) => setNewUser({ ...newUser, contact_number: e.target.value })}
          placeholder="Contact Number"
        />
        <input
          type="text"
          value={newUser.name}
          onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
          placeholder="Name"
        />
        <input
          type="text"
          value={newUser.address}
          onChange={(e) => setNewUser({ ...newUser, address: e.target.value })}
          placeholder="Address"
        />
        <input
          type="password"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          placeholder="Password"
        />
        <select
          value={newUser.role}
          onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        <button onClick={handleAddUser}>Add User</button>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h2>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Contact Number</th>
            <th>Role</th>
            <th>Enable</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.contact_number}>
              <td>{user.name}</td>
              <td>{user.contact_number}</td>
              <td>
                <select
                  value={user.role}
                  onChange={(e) => handleRoleChange(user.contact_number, e.target.value)}
                >
                  <option value="user">User</option>
                  <option value="admin">Admin</option>
                </select>
              </td>
              <td>
                <button onClick={() => handleToggleEnable(user.contact_number, user.enable)}>
                  {user.enable ? "Disable" : "Enable"}
                </button>
              </td>
              <td>
                <button onClick={() => handleRemoveUser(user.contact_number)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPage;
