import React, { useState } from 'react';
import axios from 'axios';
import campaigns from './campaigns'; // Import campaigns data

const App = () => {
  const [formData, setFormData] = useState({
    month: '',
    state: '',
    district: '',
    village: '',
    block: '',
    campaigns: campaigns.map((campaign) => ({
      id: campaign.id,
      quantity: '',
      amount: '',
    })),
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCampaignChange = (id, field, value) => {
    const updatedCampaigns = formData.campaigns.map((campaign) =>
      campaign.id === id ? { ...campaign, [field]: value } : campaign
    );
    setFormData({ ...formData, campaigns: updatedCampaigns });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put('http://localhost:8001/update', formData);
      alert(response.data.message);
      // Reset form
      setFormData({
        month: '',
        state: '',
        district: '',
        village: '',
        block: '',
        campaigns: campaigns.map((campaign) => ({
          id: campaign.id,
          quantity: '',
          amount: '',
        })),
      });
    } catch (error) {
      console.error('Error updating the data:', error);
      alert('Failed to update the data.');
    }
  };

  return (
    <div>
      <h1>Service Campaign Form</h1>
      <form onSubmit={handleSubmit}>
        {/* Section 1 */}
        <label>Month</label>
        <input
          type="text"
          name="month"
          value={formData.month}
          onChange={handleInputChange}
          required
        />

        <label>State</label>
        <input
          type="text"
          name="state"
          value={formData.state}
          onChange={handleInputChange}
          required
        />

        <label>District</label>
        <input
          type="text"
          name="district"
          value={formData.district}
          onChange={handleInputChange}
          required
        />

        <label>Village</label>
        <input
          type="text"
          name="village"
          value={formData.village}
          onChange={handleInputChange}
          required
        />

        <label>Block</label>
        <input
          type="text"
          name="block"
          value={formData.block}
          onChange={handleInputChange}
          required
        />

        {/* Section 2 */}
        <h2>Campaigns</h2>
        {campaigns.map((campaign) => (
          <div key={campaign.id} style={{ marginBottom: '1rem' }}>
            <label>
              {campaign.name}
            </label>
            <div>
              <input
                type="number"
                placeholder="Quantity"
                value={
                  formData.campaigns.find((c) => c.id === campaign.id).quantity
                }
                onChange={(e) =>
                  handleCampaignChange(campaign.id, 'quantity', e.target.value)
                }
              />
              <input
                type="number"
                placeholder="Amount"
                value={
                  formData.campaigns.find((c) => c.id === campaign.id).amount
                }
                onChange={(e) =>
                  handleCampaignChange(campaign.id, 'amount', e.target.value)
                }
              />
            </div>
          </div>
        ))}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default App;
