import React, { useState, useEffect } from 'react';

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ contact_number: "", name: "", address: "", password: "" });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:8001/users');
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        const data = await response.json();
        setUsers(data);
      } catch (err) {
        setError(err.message || 'Error fetching users');
      }
    };

    fetchUsers();
  }, []);

  const handleAddUser = async () => {
    const { contact_number, name, address, password } = newUser;
    if (!contact_number || !name || !address || !password) {
      setError("All fields are required.");
      return;
    }

    try {
      const response = await fetch('http://localhost:8001/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newUser),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || 'Failed to add user');
      }

      const addedUser = await response.json();
      setUsers((prevUsers) => [...prevUsers, addedUser]);
      setNewUser({ contact_number: "", name: "", address: "", password: "" });
      setError("");
    } catch (err) {
      setError(err.message || 'Error adding user');
    }
  };

  const handleRemoveUser = async (contact_number) => {
    try {
      const response = await fetch(`http://localhost:8001/users/${contact_number}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to remove user');
      }

      setUsers((prevUsers) => prevUsers.filter((user) => user.contact_number !== contact_number));
    } catch (err) {
      setError(err.message || 'Error removing user');
    }
  };

  return (
    <div>
      <h1>Admin Page</h1>
      <div>
        <input
          type="text"
          value={newUser.contact_number}
          onChange={(e) => setNewUser({ ...newUser, contact_number: e.target.value })}
          placeholder="Contact Number"
        />
        <input
          type="text"
          value={newUser.name}
          onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
          placeholder="Name"
        />
        <input
          type="text"
          value={newUser.address}
          onChange={(e) => setNewUser({ ...newUser, address: e.target.value })}
          placeholder="Address"
        />
        <input
          type="password"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          placeholder="Password"
        />
        <button onClick={handleAddUser}>Add User</button>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h2>Users</h2>
      <ul>
        {users.map((user) => (
          <li key={user.contact_number}>
            {user.name} ({user.contact_number}) 
            <button onClick={() => handleRemoveUser(user.contact_number)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
