import React, { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select"; // Import react-select
import campaigns from "./campaigns"; // Import the campaigns list
import statesDistricts from "./statesDistricts.json"; // Import the JSON file for states and districts
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    month: "",
    state: "",
    district: "",
    village: "",
    customVillage: "",
    block: "",
    campaignsData: {},
  });

  const [states, setStates] = useState([]);
  const [districtOptions, setDistrictOptions] = useState([]);
  const [villageOptions, setVillageOptions] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [submittedData, setSubmittedData] = useState(null);

  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  useEffect(() => {
    const uniqueStates = [
      ...new Set(
        statesDistricts.map((item) => item.STATE.toLowerCase())
      ),
    ];
    setStates(uniqueStates.sort());

    const initialCampaignsData = {};
    campaigns.forEach((campaign) => {
      initialCampaignsData[campaign.id] = { quantity: "", amount: "" };
    });
    setFormData((prev) => ({ ...prev, campaignsData: initialCampaignsData }));
  }, []);

  const handleStateChange = (e) => {
    const selectedState = e.target.value.toLowerCase();
    setFormData({ ...formData, state: selectedState, district: "", village: "" });

    const filteredDistricts = [
      ...new Set(
        statesDistricts
          .filter((item) => item.STATE.toLowerCase() === selectedState)
          .map((item) => item.DISTRICT.toLowerCase())
      ),
    ].sort();

    const districtOptions = filteredDistricts.map((district) => ({
      value: district,
      label: district.charAt(0).toUpperCase() + district.slice(1),
    }));
    setDistrictOptions(districtOptions);
  };

  const handleDistrictChange = (selectedOption) => {
    const selectedDistrict = selectedOption.value;
    setFormData({ ...formData, district: selectedDistrict, village: "" });

    const filteredVillages = [
      ...new Set(
        statesDistricts
          .filter((item) => item.DISTRICT.toLowerCase() === selectedDistrict)
          .map((item) => item.Village.toLowerCase())
      ),
    ].sort();

    const villageOptions = filteredVillages.map((village) => ({
      value: village,
      label: village.charAt(0).toUpperCase() + village.slice(1),
    }));

    villageOptions.push({ value: "other", label: "Other" }); // Add "Other" option
    setVillageOptions(villageOptions);
  };

  const handleVillageChange = (selectedOption) => {
    const selectedVillage = selectedOption.value;
    setFormData({ ...formData, village: selectedVillage, customVillage: "" });

    if (selectedVillage === "other") {
      setFormData({ ...formData, village: "other" });
    }
  };

  const handleCustomVillageChange = (e) => {
    setFormData({ ...formData, customVillage: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedData(formData);
    setShowPopup(true);
  };

  const handleConfirm = async () => {
    try {
      const response = await axios.put(
        "http://localhost:8001/update",
        submittedData
      );
      alert(response.data.message);

      setFormData({
        month: "",
        state: "",
        district: "",
        village: "",
        customVillage: "",
        block: "",
        campaignsData: campaigns.reduce((acc, campaign) => {
          acc[campaign.id] = { quantity: "", amount: "" };
          return acc;
        }, {}),
      });
      setShowPopup(false);
    } catch (error) {
      console.error("Error updating the data:", error);
      alert("Failed to update the data.");
    }
  };

  const handleCancel = () => {
    setShowPopup(false);
  };

  const getFilteredData = (data) => {
    const filteredCampaigns = Object.entries(data.campaignsData || {}).filter(
      ([_, details]) => details.quantity || details.amount
    );

    return {
      ...data,
      campaignsData: Object.fromEntries(filteredCampaigns),
    };
  };

  const filteredData = submittedData ? getFilteredData(submittedData) : null;

  return (
    <div className="container">
      <h1>Service Campaign Form</h1>
      <form onSubmit={handleSubmit}>
        <div className="white-box">
          <h2>Location Details</h2>
          <label>Month</label>
          <select
            value={formData.month}
            onChange={(e) => setFormData({ ...formData, month: e.target.value })}
            required
          >
            <option value="">Select a month</option>
            {months.map((month) => (
              <option key={month} value={month}>
                {month}
              </option>
            ))}
          </select>

          <label>State</label>
          <select value={formData.state} onChange={handleStateChange} required>
            <option value="">Select a state</option>
            {states.map((state) => (
              <option key={state} value={state}>
                {state.charAt(0).toUpperCase() + state.slice(1)}
              </option>
            ))}
          </select>

          <label>District</label>
          <Select
            options={districtOptions}
            onChange={handleDistrictChange}
            placeholder="Select a district"
            isSearchable
            value={districtOptions.find(
              (option) => option.value === formData.district
            )}
            required
          />

          <label>Village/Zone</label>
          <Select
            options={villageOptions}
            onChange={handleVillageChange}
            placeholder="Select a village"
            isSearchable
            value={villageOptions.find(
              (option) => option.value === formData.village
            )}
            required
          />

          {formData.village === "other" && (
            <div>
              <label>Enter Custom Village</label>
              <input
                type="text"
                value={formData.customVillage}
                onChange={handleCustomVillageChange}
                placeholder="Enter a custom village name"
                required
              />
            </div>
          )}

          <label>Block</label>
          <input
            type="text"
            value={formData.block}
            onChange={(e) =>
              setFormData({ ...formData, block: e.target.value })
            }
            required
          />
        </div>

        {/* Campaign Details */}
        <div className="white-box">
          <h2>Campaign Details</h2>
          {campaigns.map((campaign) => (
            <div key={campaign.id} className="campaign-row">
              <h3>
                {campaign.id}. {campaign.name}
              </h3>
              <div className="campaign-inputs">
                <input
                  type="number"
                  min="0"
                  placeholder="Quantity"
                  value={formData.campaignsData[campaign.id]?.quantity || ""}
                  onChange={(e) => handleFormChange(e, campaign.id, "quantity")}
                />
                <input
                  type="number"
                  min="0"
                  placeholder="Amount"
                  value={formData.campaignsData[campaign.id]?.amount || ""}
                  onChange={(e) => handleFormChange(e, campaign.id, "amount")}
                />
              </div>
            </div>
          ))}
        </div>

        <button type="submit">Submit</button>
      </form>

      {showPopup && filteredData && (
        <div className="popup">
          <div className="popup-content">
            <h3>Confirm Submission</h3>

            {/* Location Details */}
            <div className="location-details">
              <p><strong>Month:</strong> {filteredData.month}</p>
              <p><strong>State:</strong> {filteredData.state}</p>
              <p><strong>District:</strong> {filteredData.district}</p>
              <p><strong>Village:</strong> {filteredData.village === "other" ? filteredData.customVillage : filteredData.village}</p>
              <p><strong>Block:</strong> {filteredData.block}</p>
            </div>

            {/* Campaign Details Table */}
            <div className="campaign-details-table">
              <table>
                <thead>
                  <tr>
                    <th>Campaign Name</th>
                    <th>Quantity</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(filteredData.campaignsData).map(([campaignId, details]) => {
                    const campaign = campaigns.find(c => c.id === parseInt(campaignId));
                    return (
                      <tr key={campaignId}>
                        <td>{campaign ? campaign.name : "Unknown Campaign"}</td>
                        <td>{details.quantity}</td>
                        <td>{details.amount}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            <div className="popup-actions">
              <button onClick={handleConfirm}>Yes, Submit</button>
              <button onClick={handleCancel}>Edit</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
